import { getPurchaseBatch, getBatchDetails, submitIn, uploadingPic, getBuildList, getSpecialtyList, getFloorList, getResourceList, storeOut, getStewardList } from "../../../assets/js/scopeManage.js";
export default {
  data: function data() {
    return {
      options: {
        BuildName: "",
        // 楼栋
        Specialty: "",
        // 专业
        Floor: "",
        // 楼层
        ResourceName: "" // 物资类别

      },
      SpecialtyList: [],
      BuildNameList: [],
      purchaseBatchList: [],
      stewardList: [],
      purachseid: "",
      materialsType: "1",
      //入库/出库
      materialsPlan: "1",
      //计划采购/临时采购
      materialsNumber: "",
      actions: [],
      //上拉菜单--总的数据集合
      showSelect: false,
      //上拉菜单--显示
      selectNumer: 1,
      //入库采购
      buyThingTable: [],
      phone_show: false,
      photoList: [],
      timeShow: false,
      //时间模态框
      currentDate: new Date(),
      beginTime: "",
      //入库时间
      timeNumber: 1,
      materialsName: "请选择名称",
      //物资名称
      materialsThing: "请选择规格型号",
      materialsGlass: "请选择楼栋",
      needNumber: "",
      //采购需求量
      getTime: "",
      //需求时间
      alreadyNumber: "",
      //已入库
      thisNumber: "",
      //本次入库
      materialclass: "请选择楼层",
      outThing: "",
      //本次出库
      getpeople: "",
      outTime: "",
      //出库时间
      //入库临时采购
      addTableNumber: [],
      //出库表格
      delTableNumber: []
    };
  },
  created: function created() {
    var _this = this;

    getBuildList(this, function (res) {
      _this.BuildNameList = res.map(function (item) {
        return {
          name: item.text,
          value: item.text
        };
      });

      _this.BuildNameList.splice(0, 1);
    });
    getPurchaseBatch(this, function (res) {
      _this.purchaseBatchList = res.map(function (item) {
        return {
          name: item.Code,
          value: item.ID
        };
      });
    });
    getStewardList(this);
  },
  methods: {
    // 提交入库
    searchAllTable: function searchAllTable() {
      if (this.beginTime == "") {
        this.$message({
          message: "入库时间不能为空！",
          type: "warning"
        });
        return;
      }

      var listItems = [];

      if (this.materialsPlan == 1) {
        listItems = this.buyThingTable.filter(function (item) {
          return item.CurrentInNum !== "";
        });
      } else if (this.materialsPlan == 2) {
        listItems = this.addTableNumber;
      }

      var par = {
        model: {
          InType: this.materialsPlan,
          // 入库类型
          InDate: this.beginTime,
          // 入库时间
          PurchaseID: this.purachseid,
          // 批次ID
          PurchaseCode: this.materialsNumber // 批次code

        },
        listItems: listItems,
        infiles: this.photoList
      };
      submitIn(this, par, function () {});
    },
    //删除照片
    cancelPhoto: function cancelPhoto(index) {
      var _this2 = this;

      this.$dialog.confirm({
        title: "删除确认",
        message: "确认删除照片？",
        className: "delecet_photo"
      }).then(function () {
        // on confirm
        _this2.photoList.splice(index, 1);
      }).catch(function () {// on cancel
      });
    },
    //获取照片
    selectPhoto: function selectPhoto() {
      if (!this.phone_show) {
        this.phone_show = true;
      } else {
        this.phone_show = false;
      }
    },
    uploadCancel: function uploadCancel() {
      this.phone_show = false;
    },
    // 上传图片
    onRead: function onRead(file, detail) {
      uploadingPic(this, file, detail);
    },
    //采购批次
    projectSelect: function projectSelect() {
      this.showSelect = true;
      this.selectNumer = 1;
      this.actions = this.purchaseBatchList;
    },
    //物资名称
    nameSelect: function nameSelect() {
      this.showSelect = true;
      this.selectNumer = 2;
      this.actions = this.ResorceNameList;
    },
    //物资型号
    thingSelect: function thingSelect() {
      this.showSelect = true;
      this.selectNumer = 3;
      this.actions = this.SpecialtyList;
    },
    //楼栋
    glassSelect: function glassSelect() {
      this.showSelect = true;
      this.selectNumer = 4;
      this.actions = this.BuildNameList;
    },
    //楼层
    classSelect: function classSelect() {
      this.showSelect = true;
      this.selectNumer = 5;
      this.actions = this.FloorbyList;
    },
    //领料人
    peopleSelect: function peopleSelect() {
      this.showSelect = true;
      this.selectNumer = 6;
      this.actions = this.stewardList;
    },
    //需求时间
    getTimeShow: function getTimeShow() {
      this.timeShow = true;
      this.timeNumber = 2;
    },
    //出库时间
    outTimeSelect: function outTimeSelect() {
      this.timeShow = true;
      this.timeNumber = 3;
    },
    //点击上拉菜单的选项时候
    onSelect: function onSelect(item) {
      if (this.selectNumer == 1) {
        this.materialsNumber = item.name; //采购批次

        this.purachseid = item.value;
      } else if (this.selectNumer == 2) {
        this.options.ResourceName = item.name; //物资名称
      } else if (this.selectNumer == 3) {
        this.options.Specialty = item.name; //物资型号
      } else if (this.selectNumer == 4) {
        this.options.BuildName = item.name; //楼栋
      } else if (this.selectNumer == 5) {
        this.options.Floor = item.name; //楼层
      } else if (this.selectNumer == 6) {
        this.getpeople = item.name; //领料人
      }

      this.showSelect = false;
    },
    goBack: function goBack() {
      this.$router.push({
        path: "/#"
      });
    },
    //上传图片的时候
    handleRemove: function handleRemove(file, fileList) {
      console.log(file, fileList);
    },
    //上传图片的时候
    handlePictureCardPreview: function handlePictureCardPreview(file) {
      this.dialogImageUrl = file.url;
      this.dialogVisible = true;
    },
    //点击入库时间
    beginTimeSelect: function beginTimeSelect() {
      this.timeShow = true;
      this.timeNumber = 1;
    },
    //时间上拉菜单上的--确定
    closeTime: function closeTime(value) {
      if (this.timeNumber == "1") {
        console.log("110");
        this.beginTime = value.getFullYear() + "/" + (value.getMonth() + 1) + "/" + value.getDate();
      } else if (this.timeNumber == "2") {
        this.getTime = value.getFullYear() + "/" + (value.getMonth() + 1) + "/" + value.getDate();
      } else if (this.timeNumber == "3") {
        this.outTime = value.getFullYear() + "/" + (value.getMonth() + 1) + "/" + value.getDate();
      }

      this.timeShow = false;
    },
    //时间上拉菜单上的--取消
    cancel: function cancel() {
      this.timeShow = false;
    },
    formatter: function formatter(type, value) {
      if (type === "year") {
        return "".concat(value, "\u5E74");
      } else if (type === "month") {
        return "".concat(value, "\u6708");
      } else if (type === "day") {
        return "".concat(value, "\u65E5");
      }

      return value;
    },
    //点击-临时采购表格中的删除
    handleDelete: function handleDelete(index) {
      this.addTableNumber.splice(index, 1);
    },
    delDelete: function delDelete(index) {
      this.delTableNumber.splice(index, 1);
    },
    //点击-临时采购添加按钮
    addTable: function addTable() {
      if (this.materialsType == 2) {
        //出库--表格添加
        var text = {
          ResourceName: this.options.ResourceName,
          //物资名称
          Spec: this.options.Specialty,
          //型号
          BuildName: this.options.BuildName,
          //楼栋
          Floor: this.options.Floor,
          //楼层
          DemandNum: "",
          //施工预算总量
          already: "",
          //已出库
          hasNumber: "",
          //库存
          thisnumber: this.outThing //本次出库

        };
        this.delTableNumber.push(text);
      } else {
        //入库临时采购-添加
        var _text = {
          ResourceName: this.options.ResourceName,
          //物资名称
          Spec: this.options.Specialty,
          //型号
          BuildName: this.options.BuildName,
          //楼栋
          Floor: this.options.Floor,
          //楼层
          DemandTime: this.getTime,
          //需求时间
          DemandNum: this.needNumber,
          //需求总量
          InNum: this.alreadyNumber,
          //已入库
          CurrentInNum: this.thisNumber //本次入库

        };
        this.addTableNumber.push(_text);
      }
    },
    //点击出库按钮
    searchoutTable: function searchoutTable() {
      var listItems = this.delTableNumber;
      var par = {
        model: {
          OutDate: this.outTime,
          // 出库时间
          UsePerson: this.getpeople // 领料人

        },
        listItems: listItems,
        infiles: this.photoList
      };
      storeOut(this, par);
    },
    // 数据复原
    recoverData: function recoverData(val) {
      this.photoList = [];
      this.options = {
        BuildName: "",
        // 楼栋
        Specialty: "",
        // 专业
        Floor: "",
        // 楼层
        ResourceName: "" // 物资类别

      };

      if (val == 1) {
        this.outThing = "";
        this.delTableNumber = [];
        this.getpeople = "";
        this.outTime = "";
      }

      if (val == 2) {
        this.beginTime = "";
        this.materialsNumber = "";
        this.buyThingTable = [];
        this.addTableNumber = [];
        this.needNumber = "";
        this.getTime = "";
        this.alreadyNumber = "";
        this.outThing = "";
      }

      if (val == 3) {
        this.beginTime = "";
        this.materialsNumber = "";
        this.buyThingTable = [];
      }

      if (val == 4) {
        this.beginTime = "";
        this.addTableNumber = [];
        this.needNumber = "";
        this.getTime = "";
        this.alreadyNumber = "";
        this.outThing = "";
      }
    }
  },
  watch: {
    // 楼层
    "options.Floor": {
      handler: function handler(val, oldval) {
        var _this3 = this;

        if (val != "") {
          this.ResorceNameList = [];
          this.options.ResourceName = "";
          getResourceList(this, function (res) {
            _this3.ResorceNameList = res.map(function (item) {
              return {
                name: item.text,
                value: item.text
              };
            });
          });
          this.ResorceNameList.splice(0, 1);
        }
      }
    },
    // 专业
    "options.Specialty": {
      handler: function handler(val, oldval) {
        var _this4 = this;

        if (val != "") {
          this.FloorbyList = [];
          this.options.Floor = "";
          this.ResorceNameList = [];
          this.options.ResourceName = "";
          getFloorList(this, function (res) {
            _this4.FloorbyList = res.map(function (item) {
              return {
                name: item.text,
                value: item.text
              };
            });
          });
          this.FloorbyList.splice(0, 1);
        }
      }
    },
    "options.BuildName": function optionsBuildName(val) {
      var _this5 = this;

      this.SpecialtyList = [];
      this.options.Specialty = "";
      this.FloorbyList = [];
      this.options.Floor = "";
      this.ResorceNameList = [];
      this.options.ResourceName = "";
      getSpecialtyList(this, function (res) {
        _this5.SpecialtyList = res.map(function (item) {
          return {
            name: item.text,
            value: item.text
          };
        });

        _this5.SpecialtyList.splice(0, 1);
      });
    },
    materialsNumber: function materialsNumber(val) {
      if (val !== "") {
        getBatchDetails(this, {
          purachseid: this.purachseid
        });
      }
    },
    materialsType: function materialsType(val) {
      this.recoverData(val);
    },
    materialsPlan: function materialsPlan(val) {
      if (val == 1) {
        this.recoverData(3);
      }

      if (val == 2) {
        this.recoverData(4);
      }
    }
  }
};
